import React from 'react';

const TaskItem = ({ task, onDelete, onToggle }) => {
  return (
    <div className={`task ${task.completed ? 'completed' : ''}`} onDoubleClick={() => onToggle(task._id)}>
      <h3>
        {task.title}{' '}
        <button onClick={() => onDelete(task._id)} style={{ color: 'red', cursor: 'pointer' }}>
          X
        </button>
      </h3>
    </div>
  );
};

export default TaskItem;
