import './App.css';
import { useState, useEffect } from 'react';
import axios from 'axios';
import TaskForm from './components/TaskForm';
import TaskList from './components/TaskList';

function App() {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchTasks = async () => {
      const { data } = await axios.get('/api/tasks');
      setTasks(data);
    };
    fetchTasks();
  }, []);

  const addTask = async (task) => {
    const { data } = await axios.post('/api/tasks', task);
    setTasks([...tasks, data]);
  };

  const deleteTask = async (id) => {
    await axios.delete(`/api/tasks/${id}`);
    setTasks(tasks.filter((task) => task._id !== id));
  };

  const toggleComplete = async (id) => {
    const taskToToggle = await tasks.find((task) => task._id === id);
    const updatedTask = { ...taskToToggle, completed: !taskToToggle.completed };

    const { data } = await axios.put(`/api/tasks/${id}`, updatedTask);

    setTasks(
      tasks.map((task) =>
        task._id === id ? { ...task, completed: data.completed } : task
      )
    );
  };

  return (
    <div className="App">
      <h1>MERN To-Do List</h1>
      <TaskForm onAdd={addTask} />
      {tasks.length > 0 ? (
        <TaskList tasks={tasks} onDelete={deleteTask} onToggle={toggleComplete} />
      ) : (
        'No tasks to show'
      )}
    </div>
  );
}

export default App;
